mapper.select:
select count(*) from t_note_like where status=1 and note_id in (
    select note_id from t_note where creator_id=#{userId} and status=1
    )

select count(*) from t_note_collection where status=1 and note_id in(
    select note_id from t_note where creator_id=#{userId} and status=1
    )
